import React, { useEffect, useState } from 'react';

function SeatmapPage({ movieId, onProceedToPurchase }) {
  const [seatmap, setSeatmap] = useState([]);
  const [selectedSeats, setSelectedSeats] = useState([]);

  useEffect(() => {
    const fetchSeatmap = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:5000/movies/${movieId}/seatmap`);
        if (response.ok) {
          const data = await response.json();
          setSeatmap(data.rows); // Set seatmap rows
        } else {
          console.error('Failed to fetch seatmap:', response.status);
        }
      } catch (error) {
        console.error('Error fetching seatmap:', error);
      }
    };
    fetchSeatmap();
  }, [movieId]);

  const handleSeatClick = (row, seatNumber) => {
    const seatKey = `${row}${seatNumber}`;
    setSelectedSeats((prev) =>
      prev.includes(seatKey) ? prev.filter((key) => key !== seatKey) : [...prev, seatKey]
    );
  };

  const handleProceedToPurchase = async () => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/movies/${movieId}/seatmap/update`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ selected_seats: selectedSeats }),
      });
      if (response.ok) {
        onProceedToPurchase(selectedSeats); // Trigger parent callback
      } else {
        console.error('Failed to update seatmap:', response.status);
      }
    } catch (error) {
      console.error('Error updating seatmap:', error);
    }
  };


  return (
    <div>
      <h1>Seatmap for Movie ID: {movieId}</h1>
      <div className="seatmap">
        {seatmap.map((row, rowIndex) => (
          <div key={rowIndex} className="seat-row">
            <span className="row-label">{row.row}:</span>
            {row.seats.map((seat, seatIndex) => (
              <button
                key={seatIndex}
                onClick={() => handleSeatClick(row.row, seat.number)}
                style={{
                  backgroundColor: selectedSeats.includes(`${row.row}${seat.number}`)
                    ? 'green'
                    : seat.available
                    ? 'gray'
                    : 'red',
                  margin: '5px',
                  padding: '10px',
                  cursor: seat.available ? 'pointer' : 'not-allowed',
                }}
                disabled={!seat.available}
              >
                {seat.number}
              </button>
            ))}
          </div>
        ))}
      </div>
      <button
        onClick={onProceedToPurchase}
        disabled={selectedSeats.length === 0}
        style={{
          marginTop: '20px',
          padding: '10px 20px',
          backgroundColor: selectedSeats.length > 0 ? 'blue' : 'lightgray',
          color: 'white',
          cursor: selectedSeats.length > 0 ? 'pointer' : 'not-allowed',
        }}
      >
        Proceed to Purchase
      </button>
    </div>
  );


}

export default SeatmapPage;
