import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const MoviePage = () => {
  const [movies, setMovies] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('/movies')
      .then((response) => response.json())
      .then((data) => setMovies(data))
      .catch((error) => console.error('Error fetching movies:', error));
  }, []);

  const handleViewSeatmap = (movieId) => {
    navigate(`/movies/${movieId}/seatmap`);
  };

  return (
    <div>
      <h1>Movies</h1>
      <div>
        {movies.map((movie) => (
          <div key={movie.id} style={{ border: '1px solid black', margin: '10px', padding: '10px' }}>
            <h2>{movie.name}</h2>
            <p>{movie.description}</p>
            <p>Price: ${movie.price}</p>
            <img src={movie.image} alt={movie.name} width="200" />
            <button onClick={() => handleViewSeatmap(movie.id)}>View Seatmap</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MoviePage;
