import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const SeatMapPage = () => {
  const { movieId } = useParams();
  const [seatmap, setSeatmap] = useState([]);
  const [movieName, setMovieName] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`/movies/${movieId}/seatmap`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Seatmap not found');
        }
        return response.json();
      })
      .then((data) => setSeatmap(data))
      .catch((error) => setError(error.message));

    fetch(`/movies/${movieId}`)
      .then((response) => response.json())
      .then((data) => setMovieName(data.name))
      .catch(() => setMovieName('Unknown Movie'));
  }, [movieId]);

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h1>Seatmap for {movieName}</h1>
      {seatmap.length === 0 ? (
        <p>No seatmap available.</p>
      ) : (
        <table border="1" style={{ borderCollapse: 'collapse', width: '50%' }}>
          <thead>
            <tr>
              <th>Row</th>
              <th>Seats</th>
            </tr>
          </thead>
          <tbody>
            {seatmap.map((row) => (
              <tr key={row.row}>
                <td>{row.row}</td>
                <td>
                  {row.seats.map((seat) => (
                    <span
                      key={seat.number}
                      style={{
                        color: seat.available ? 'green' : 'red',
                        margin: '0 5px',
                      }}
                    >
                      {seat.number} ({seat.available ? 'Available' : 'Taken'})
                    </span>
                  ))}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default SeatMapPage;
