// Productpage.js
import React, { useState, useEffect } from 'react';
import Header from './Header';
import ProductList from './ProductList';
import Cart from './Cart';
import Footer from './Footer';

const Productpage = () => {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const savedCartItems = JSON.parse(localStorage.getItem('cartItems'));
    if (savedCartItems) {
      setCartItems(savedCartItems);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('cartItems', JSON.stringify(cartItems));
  }, [cartItems]);

  const handleAddToCart = (product) => {
    const existingItem = cartItems.find(item => item.name === product.name);
    if (existingItem) {
      const updatedCartItems = cartItems.map(item =>
        item.name === product.name ? { ...item, quantity: item.quantity + 1 } : item
      );
      setCartItems(updatedCartItems);
    } else {
      const newCartItem = { ...product, quantity: 1, total: product.price };
      setCartItems([...cartItems, newCartItem]);
    }
  };

  const handleRemoveFromCart = (itemToRemove) => {
    const updatedCartItems = cartItems.map(item =>
      item.name === itemToRemove.name ? { ...item, quantity: item.quantity - 1 } : item
    ).filter(item => item.quantity > 0);
    setCartItems(updatedCartItems);
  };

  return (
    <div className="product-page" style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      <Header />
      <div className="content-wrapper" style={{ display: 'flex', flex: 1 }}>
        <ProductList onAddToCart={handleAddToCart} />
        <div className="cart-container" style={{ width: '300px', marginLeft: 'auto' }}>
          <Cart cartItems={cartItems} onRemove={handleRemoveFromCart} />
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Productpage;
