import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const SeatMapPage = () => {
  const { movieId } = useParams();
  const [seatMap, setSeatMap] = useState({});
  const [selectedSeat, setSelectedSeat] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch seat map
    fetch(`http://127.0.0.1:5000/movies/${movieId}/seats`)
      .then(response => response.json())
      .then(data => setSeatMap(data));
  }, [movieId]);

  const handleSeatSelect = (row, seat) => {
    setSelectedSeat(`${row}${seat}`);
  };

  const handlePurchase = () => {
    fetch('http://127.0.0.1:5000/purchase', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ user: "user1", movie_id: movieId, seat: selectedSeat }),
    })
      .then(response => response.json())
      .then(data => navigate('/mytickets'));
  };

  return (
    <div>
      <h2>Select a Seat for Movie {movieId}</h2>
      {Object.keys(seatMap).map(row =>
        Object.keys(seatMap[row]).map(seat => (
          <button
            key={seat}
            disabled={seatMap[row][seat] !== 'available'}
            onClick={() => handleSeatSelect(row, seat)}
          >
            {row}{seat} ({seatMap[row][seat]})
          </button>
        ))
      )}
      <div>
        <button onClick={handlePurchase} disabled={!selectedSeat}>
          Purchase
        </button>
      </div>
    </div>
  );
};

export default SeatMapPage;
