import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const SeatMapPage = () => {
  const { movieId } = useParams(); // Get movie ID from URL
  const navigate = useNavigate();
  const [seatmap, setSeatmap] = useState([]); // Store the seat map
  const [selectedSeats, setSelectedSeats] = useState([]); // Track selected seats

  useEffect(() => {
    // Fetch seatmap for the selected movie
    const fetchSeatmap = async () => {
      const response = await fetch(`http://127.0.0.1:5000/movies/${movieId}/seatmap`);
      const data = await response.json();
      setSeatmap(data.rows); // Set the seatmap data
    };
    fetchSeatmap();
  }, [movieId]);

  const handleSeatSelection = (row, seat) => {
    // Toggle seat selection
    const seatKey = `${row}-${seat.number}`;
    if (selectedSeats.includes(seatKey)) {
      setSelectedSeats(selectedSeats.filter((s) => s !== seatKey));
    } else if (seat.available) {
      setSelectedSeats([...selectedSeats, seatKey]);
    }
  };

  const handleProceedToPurchase = () => {
    navigate('/purchase', { state: { movieId, selectedSeats } });
  };

  return (
    <div>
      <h2>Seat Selection</h2>
      <div className="seatmap">
        {seatmap.map((row, rowIndex) => (
          <div key={rowIndex} className="seat-row">
            <span className="row-label">{row.row}</span>
            {row.seats.map((seat, seatIndex) => {
              const seatKey = `${row.row}-${seat.number}`;
              return (
                <button
                  key={seatIndex}
                  onClick={() => handleSeatSelection(row.row, seat)}
                  style={{
                    backgroundColor: selectedSeats.includes(seatKey) ? 'green' : seat.available ? 'gray' : 'red',
                    margin: '5px',
                    padding: '10px',
                    cursor: seat.available ? 'pointer' : 'not-allowed',
                  }}
                  disabled={!seat.available}
                >
                  {seat.number}
                </button>
              );
            })}
          </div>
        ))}
      </div>
      <button
        onClick={handleProceedToPurchase}
        disabled={selectedSeats.length === 0}
        style={{
          marginTop: '20px',
          padding: '10px 20px',
          cursor: selectedSeats.length > 0 ? 'pointer' : 'not-allowed',
        }}
      >
        Proceed to Purchase
      </button>
    </div>
  );
};

export default SeatMapPage;
